mapper.insert:
INSERT INTO illness (medical_card_id, type_id, heaviness, appearance_dttm, recovery_dt)
VALUES (#{medicalCardId, jdbcType=BIGINT}, #{typeId, jdbcType=BIGINT}, #{heaviness, jdbcType=CHARACTER},
        #{appearanceDttm, jdbcType=TIMESTAMP}, #{recoveryDt, jdbcType=DATE});

mapper.delete:
DELETE
FROM illness
WHERE id = #{illnessId, jdbcType=BIGINT};
