mapper.insert:
INSERT INTO illness (medical_card_id, type_id, heaviness, appearance_dttm, recovery_dt)
VALUES (#{medicalCardId}, #{typeId}, #{heaviness},
        #{appearanceDttm}, #{recoveryDt});

update.if:
UPDATE illness SET

    medical_card_id = #{illness.medicalCardId}


    type_id = #{illness.typeId}


    heaviness = #{illness.heaviness}


    appearance_dttm = #{illness.appearanceDttm}


    recovery_dt = #{illness.recoveryDt}

WHERE id = #{illness.id};

mapper.delete:
DELETE
FROM illness
WHERE id = #{illnessId, jdbcType=BIGINT};
